import React, { Component } from 'react'

class Login extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      cpf: '',
    };
  }


  handleChange(event) {
    this.setState({
      estadoFavorito: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <form className="form">
        <h1>Register √Årea</h1>
          <label htmlFor="email-input">
              Name
            <input
              className="login__login_input"
              type="text"
              value={ this.name }
              onChange={ ({ target: { value } }) => this.name(value) }
              data-testid="login__login_input"
              placeholder="Full Name"
            />
          </label>
          <label htmlFor="password-input">
              CPF
            <input
              type="cpf"
              value={ this.cpf }
              onChange={ ({ target: { value } }) => this.cpf(value) }
              data-testid="login_cpf_input"
              placeholder="White your CPF"
            />
          </label>
        </form>
      </div>
    );
  }
}

export default Login;
