import React, { Component } from 'react'

class Login extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      cpf: '',
    };
  }

  handleChange(event) {
    this.setState({
      estadoFavorito: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <form className="form">
        <fieldset>
        <h2>Área do Usuário</h2>
        <div>
          <label htmlFor="email-input">
              Nome
            <input
              className="login__login_input"
              type="text"
              // value={ this.name }
              // onChange={ ({ target: { value } }) => this.name(value) }
              placeholder="Full Name"
            />
          </label>
          </div>
          <div>
          <label htmlFor="password-input">
              CPF
            <input
              type="number"
              // value={ this.cpf }
              // onChange={ ({ target: { value } }) => this.cpf(value) }
              placeholder="White your CPF"
            />
          </label>
          </div>
          <div>
                <button 
                type="submit"
                >
                    Entrar
                </button>
            </div>
          </fieldset>
        </form>
      </div>
    );
  }
}

export default Login;
